import React, { useState } from 'react';
import { useContext } from 'react';
import TaskContext from '../../context/TaskContext';
import TokenContext from '../../context/TokenContext';
import axios from "../../Axios/axios.js";
import "./createTask.css";

function CreateTask() {
    const { dispatch } = useContext(TaskContext);
    const { userToken } = useContext(TokenContext);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [toast, setToast] = useState(null);

    const handleAdd = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post("/task/addTask", { title, description }, {
                headers: {
                    Authorization: `Bearer ${userToken}`
                }
            });
            setToast({ message: res.data, type: 'success' });
            dispatch({
                type: "ADD_TASK",
                title,
                description
            });
            setTitle("");
            setDescription("");
        } catch (error) {
            setToast({ message: 'Failed to add task', type: 'error' });
            console.log(error);
        }
    }

    return (
        <div className="addContainer md:w-1/3 md:mx-auto mx-3 mt-5 flex justify-center">
            <div className='w-11/12 p-5 shadow-lg rounded-lg bg-white dark:bg-gray-800'>
                <h2 className='text-2xl font-semibold text-center mb-4 text-gray-900 dark:text-white'>Create New Task</h2>
                <form onSubmit={handleAdd}>
                    <div className='mb-4'>
                        <label htmlFor="title" className='block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300'>Title</label>
                        <input
                            type="text"
                            name="title"
                            id="title"
                            value={title}
                            required
                            onChange={(e) => setTitle(e.target.value)}
                            className='bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500'
                        />
                    </div>
                    <div className='mb-4'>
                        <label htmlFor="description" className='block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300'>Description</label>
                        <textarea
                            rows={5}
                            name="description"
                            id="description"
                            value={description}
                            required
                            onChange={(e) => setDescription(e.target.value)}
                            style={{ resize: "none" }}
                            className='bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500'
                        />
                    </div>
                    <div className='flex justify-center'>
                        <button
                            type='submit'
                            className='bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 text-white font-semibold rounded-md px-5 py-2.5 focus:outline-none dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800'
                        >Add</button>
                    </div>
                </form>
                {toast && (
                    <div className={`toast p-3 rounded-xl shadow-2xl text-center fixed bottom-4 left-1/2 transform -translate-x-1/2 ${toast.type === 'success' ? 'bg-green-600' : 'bg-red-600'} text-white animate-toast`}>
                        <p>{toast.message}</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default CreateTask;
