import React, { useState, useContext } from 'react';
import TaskContext from '../../context/TaskContext';
import TokenContext from '../../context/TokenContext';
import axios from "../../Axios/axios.js";
import "./createTask.css";

function CreateTask() {
    const { dispatch } = useContext(TaskContext);
    const { userToken } = useContext(TokenContext);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [toast, setToast] = useState(null);

    const handleAdd = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post("/task/addTask", { title, description }, {
                headers: {
                    Authorization: `Bearer ${userToken}`
                }
            });
            setToast({ message: res.data.message, type: 'success' });
            dispatch({
                type: "ADD_TASK",
                title,
                description
            });
            setTitle("");
            setDescription("");
        } catch (error) {
            setToast({ message: 'Failed to add task', type: 'error' });
            console.log(error);
        }
    };

    return (
        <div className="addContainer flex justify-center relative">
            <div className='formContainer w-full max-w-md p-6 bg-white shadow-lg rounded-lg z-10'>
                <h2 className='text-2xl font-semibold mb-4'>Create New Task</h2>
                <form onSubmit={handleAdd}>
                    <div className='mb-4'>
                        <label htmlFor="title" className='block text-sm font-medium mb-2'>Title</label>
                        <input
                            type="text"
                            name="title"
                            id="title"
                            value={title}
                            required
                            onChange={(e) => setTitle(e.target.value)}
                            className='w-full p-3 border rounded-lg'
                        />
                    </div>
                    <div className='mb-4'>
                        <label htmlFor="description" className='block text-sm font-medium mb-2'>Description</label>
                        <textarea
                            rows={5}
                            name="description"
                            id="description"
                            value={description}
                            required
                            onChange={(e) => setDescription(e.target.value)}
                            className='w-full p-3 border rounded-lg resize-none'
                        />
                    </div>
                    <div className='flex justify-center'>
                        <button
                            type='submit'
                            className='bg-blue-700 text-white px-4 py-2 rounded-lg hover:bg-blue-800'
                        >
                            Add
                        </button>
                    </div>
                </form>
                {toast && (
                    <div className={`toast p-3 rounded-xl shadow-2xl text-center absolute bottom-4 left-1/2 transform -translate-x-1/2 ${toast.type === 'success' ? 'bg-green-600' : 'bg-red-600'} text-white`}>
                        <p>{toast.message}</p>
                    </div>
                )}
            </div>
            <div className="absolute inset-0 z-0">
                <img
                    src="https://source.unsplash.com/random/800x600"
                    alt="Background"
                    className="w-full h-full object-cover opacity-30"
                />
            </div>
        </div>
    );
}

export default CreateTask;
