import React from 'react';
import TaskIndicator from './TaskIndicator';
import CreateTask from './createTask/CreateTask';
import { Outlet } from 'react-router-dom';
import './layout.css';

function Layout() {
    return (
        <div className="min-h-screen bg-gray-100 p-5 flex flex-col items-center md:flex-row md:justify-around">
            <div className="w-full md:w-1/3 p-5 bg-white shadow-md rounded-lg mb-5 md:mb-0">
                <CreateTask />
            </div>
            <div className="w-full md:w-2/3 p-5 bg-white shadow-md rounded-lg">
                <div className="mb-5">
                    <Outlet />
                </div>
                <div>
                    <TaskIndicator />
                </div>
            </div>
        </div>
    );
}

export default Layout;
